import '../styles/App.css';
import {userState} from 'react';

function App() {
const [firstNumber, setFirstNumber]= userState(0);
const[secondNumber, setSecondNumber]= userState(0);

const handleFirstNumber= (ev) =>{
const firstInput=ev.target.value;
setFirstNumber(firstInput)
}
const handleSecondNumber=(ev) =>{
  const secondInput=ev.target.value;
  setSecondNumber(secondInput);
}

const total= parseInt(firstNumber) + parseInt(secondNumber);
  return (

      <div>
        <h1>La calculadora</h1>
        <label htmlFor="first-input"> Escribe un número:
        <input type="text" onChange={handleFirstNumber} />
          </label><label htmlFor="second-input">Escribe otro número:
          <input type="text" onChange={handleSecondNumber} /></label>
          <p>La suma de <strong>{firstNumber}</strong> y <strong>{secondNumber}</strong> es <strong>{total}</strong></p>

      </div>

  );
}

export default App;
