const App = () => {



  return (


      <div className="container">
      <h1 className="title">Adivina el número</h1>
      <htmlForm action="">
        <label className="label" htmlFor="usernumber"> Introduce aquí tu número</label>
        <input
          className="input js-input"
          placeholder="Introduce un número de 0 a 100"
          type="text"
          id="usernumber"
          required
          />
        <button className="button js-button">Prueba</button>
      </htmlForm>
      <p className="js-clue clue">Pista: Escribe el número y dale a prueba</p>
      <p className="js-counter counter">Número de intentos: 0</p>


    </div>
  );
};

export default App;
