import { useState } from 'react';
import '../styles/main.scss';

function App() {
const [input, setInput]=useState('');
const [resultA, setResultA]=useState('');
const [resultB, setResultB]=useState('')
const handleCheckbox=(ev)=>{
const inputValue=ev.target.id;
setInput(inputValue)
//console.log(input);
if (inputValue===('Patatas' && 'Huevos' && 'Cebolla')){
  resultA='Eres una persona concebollista.';
  setResultA(resultA)
console.log('Eres una persona concebollista.');
}else{
  resultB='Eres un robot sin paladar';
  setResultB(resultB)
  console.log('Eres un robot sin paladar')
}
}

  return (

<>
<h1> Selecciona los ingrediente de la tortilla de patatas</h1>

<form action="" onChange={handleCheckbox}>
<input type="checkbox" id="Macarrones" name="ingredient"  /> Macarrones
  <input type="checkbox" id="Patatas" name="ingredient"  /> Patatas
  <input type="checkbox" id="Nueces" name="ingredient"  /> Nueces
  <input type="checkbox" id="Huevos" name="ingredient"  /> Huevos
  <input type="checkbox" id="Cebolla" name="ingredient"  /> Cebolla
  <input type="checkbox" id="Cerveza" name="ingredient"  /> Cerveza
  <p>`${input}`</p>
</form>

</>

  );
}

export default App;
