// Fichero src/services/api.js
const callToApi = (searchName) => {
    // Recibimos por par치metros searchName, que nos lo pasan desde App dentro del useEffect
    // Llamamos al fetch interpolando la URL del API m치s el nombre buscado
    return fetch(`https://api.tvmaze.com/search/shows?q=paranormal${searchName}`)
      .then(response => response.json())
      .then((dataApi)=> {
        const shows = dataApi.results.map((data) => {
          return {
            name: data.show.name
          };
        });
        // Retornamos los resultados del API al componente App
        return shows;
      });
  };

  export default callToApi;
