    'Ir a la puerta de un gimnasio',
    'Comerme las croquetas mirando a la gente que entra en el gimnasio'
  ];
const RenderToDoList=(tasks)=>{
  const ToDoList=tasks.map(
    (task,index)=>{ 
      return(
        <li key={index}>{task}</li>

      )
   }
  )
}
  return (

<>
{title}

  <ol>{RenderToDoList()}</ol>

</>

  );
}

