const handleInput=(ev)=>{
  const userInput=ev.target.value;
setName(userInput);
if (userInput.length>0){
  setHide('')
}

}
  return (

      <>
      <form action="">
        <label htmlFor="name">Escribe un nombre</label>
        <input type="text" name="name" id="name" placeholder="María García" onChange={handleInput} />
        <p className="${hide}"> A ti te conozco! tu eres {name}</p>
      </form>


      </>


  );
}

export default App;
